catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

	var ga = new GlideAjax('x_tivit_user_manag.AjaxUtils');
    ga.addParam('sysparm_name', 'getUserByMail');
    ga.addParam('userMail', newValue);
    ga.getXMLAnswer(ajaxResponse);

}

function ajaxResponse(answer) {

		if (answer == 'true'){
			g_form.addErrorMessage('E-Mail '+ newValue +' já está cadastrado!');
			g_form.clearValue('email');
		}else{
			g_form.setValue('user_id',newValue);
		}
}
