catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {

	g_form.setMandatory('user_id',false);
	g_form.setMandatory('email',false);
	g_form.setMandatory('nome',false);
	g_form.setMandatory('user_roles',false);
	g_form.setMandatory('user_change',false);
	g_form.setMandatory('user_del',false);
	g_form.setMandatory('user_active',false);

	g_form.setContainerDisplay('criar',false);
	g_form.setContainerDisplay('alterar',false);
	g_form.setContainerDisplay('deletar',false);
	g_form.setContainerDisplay('ativar_cs',false);
	g_form.setContainerDisplay('trocar_cs',false);

	if (isLoading || newValue == '') {
		return;
	}

	g_form.setContainerDisplay('user_management',true);

	if(g_form.getValue('opera_o') == 'Criar'){
		g_form.setContainerDisplay('criar',true);
		g_form.setMandatory('user_id',true);
		g_form.setMandatory('email',true);
		g_form.setMandatory('nome',true);
		g_form.setMandatory('user_roles',true);		
	}

	if(g_form.getValue('opera_o') == 'Alterar'){
		g_form.setContainerDisplay('alterar',true);
		g_form.setMandatory('user_change',true);
	}

	if(g_form.getValue('opera_o') == 'Deletar'){
		g_form.setContainerDisplay('deletar',true);
		g_form.setMandatory('user_del',true);
	}  

	if(g_form.getValue('opera_o') == 'Ativar'){
		g_form.setContainerDisplay('ativar_cs',true);
		g_form.setMandatory('user_active',true);
	} 

	if(g_form.getValue('opera_o') == 'Trocar'){
		g_form.setContainerDisplay('trocar_cs',true);
	} 
}
