catalog_script_client.script:
function onSubmit() {

	var controle = 0;

	if (g_form.getValue('empresa') == 'f66b14e1c611227b0166c3a0df4046ff'){
		if (g_form.getValue('opera_o') == 'Criar'){
			if (g_form.getValue('user_roles') == 'Aprovador' || g_form.getValue('user_roles') == 'Solucionador'){
				controle = 1;
			}
		}else if (g_form.getValue('opera_o') == 'Alterar'){
			if (g_form.getValue('u_user_roles') == 'Aprovador' || g_form.getValue('u_user_roles') == 'Solucionador'){
				controle = 1;
			}
		}else if (g_form.getValue('opera_o') == 'Ativar')
			if (g_form.getValue('user_roles_active') == 'Aprovador' || g_form.getValue('user_roles_active') == 'Solucionador'){
				controle = 1;
		}
	}

	if (controle == 1 && this.document.getElementsByClassName('get-attachment').length == 0){
		alert("Para essa solicitação é obrigatório anexar o e-mail de aprovação do diretor");
		return false;
	}
}
