sys_variable_value.value:
(function execute(inputs, outputs) {

  grAddUser = new GlideRecord('sys_user');
  grAddUser.get(inputs.AddUser);

  grRemUser = new GlideRecord('sys_user');
  grRemUser.get(inputs.RemUser);

  grAddUser.setValue('u_user_roles',inputs.lcs);
  grAddUser.update();

  grRemUser.setValue('u_custom_roles',false);
  grRemUser.update();
  grRemUser.setValue('u_user_roles','Solicitante');
  grRemUser.update();

})(inputs, outputs);

