sys_script_include.script:
var AjaxUtils = Class.create();
AjaxUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getUser: function(){

		var grUser = new GlideRecord('sys_user');
		var user = this.getParameter('user');

		grUser.get(user);

		this.profifeIC('user_name',grUser.getValue('user_name'));
		this.profifeIC('u_user_roles',grUser.getDisplayValue('u_user_roles'));

	},

	getUserByMail:function(){

		var userMail = this.getParameter('userMail');
		var grUser = new GlideRecord('sys_user');

		grUser.addQuery('user_name',userMail);
		grUser.query();

		if (grUser.next())
			return true;
		else
			return false;
	},

	profifeIC: function(name, value) {
		var parm = this.newItem("Parm");
		parm.setAttribute("name",name);
		parm.setAttribute("value",value);
	},

	getRoles: function(){

		var grUser = new GlideRecord('sys_user_has_role');
		var user = this.getParameter('user');
		var role = 'Solicitante';

		grUser.addQuery('user',user);
		grUser.addEncodedQuery('user=' + user + '^role=282bf1fac6112285017366cb5f867469^ORrole=3be5d3d1b7103300dff9fa13ee11a987');
		grUser.query();

		if(grUser.next()){
			if(grUser.role == '282bf1fac6112285017366cb5f867469') role = 'Solucionador'; else role = 'Aprovador';
		}



		this.profifeIC('user_name',grUser.getValue('user_name'));
		this.profifeIC('u_user_roles',role);

	},

    type: 'AjaxUtils'
});
